mapper.select:
SELECT COUNT(class_no) FROM homeschool.tb_class c JOIN tb_user u on c.teacher_user_no = u.user_no JOIN tb_profile p ON u.user_no = p.user_no
grade_no = #{word}
subject_no = #{word}
class_title LIKE CONCAT('%', #{word}, '%')
user_name LIKE CONCAT('%', #{word}, '%')
AND user_code = #{userCode}

SELECT c.class_no , s.subject_no , subject_title , class_title , class_member_num , class_strt_dt , class_end_dt , class_status , subject_title , count(join_no) AS join_cnt FROM homeschool.tb_class c JOIN tb_subject s ON c.subject_no = s.subject_no JOIN tb_user u on c.teacher_user_no = u.user_no LEFT JOIN tb_class_join cj ON c.class_no = cj.class_no
c.grade_no = #{word}
c.subject_no = #{word}
class_title LIKE CONCAT('%', #{word}, '%')
class_strt_dt <= current_timestamp() and class_end_dt >= current_timestamp() and class_status = 'y'
class_strt_dt > current_timestamp() and class_end_dt < current_timestamp() and class_status != 'y'
AND join_status = 'y'
AND user_code = #{userCode} GROUP BY c.class_no ORDER BY c.frst_wrt_dt DESC LIMIT #{rowPerPage} OFFSET #{startRow};

mapper.insert:

SELECT user_no
FROM tb_user
WHERE user_code = #{userCode}

INSERT INTO
homeschool.tb_class
(
teacher_user_no
, subject_no
, class_title
, class_member_num
, class_strt_dt
, class_end_dt
, class_status
, frst_wrt_id
, last_wrt_id
)
VALUES
(
#{teacherUserNo}
, #{subjectNo}
, #{classTitle}
, #{classMemberNum}
, #{classStrtDt}
, #{classEndDt}
, #{classStatus}
, #{frstWrtId}
, #{lastWrtId}
);

mapper.update:
UPDATE
    homeschool.tb_class
SET
    subject_no=#{subjectNo}
  , class_title=#{classTitle}
  , class_member_num=#{classMemberNum}
  , class_strt_dt=#{classStrtDt}
  , class_end_dt=#{classEndDt}
  , class_status=#{classStatus}
  , last_wrt_dt=current_timestamp()
  , last_wrt_id=#{userNo}
WHERE class_no=#{classNo};
