mapper.select:
SELECT COUNT(lecture_no) FROM homeschool.tb_lecture
lecture_title LIKE CONCAT('%', #{word}, '%')
AND user_code = #{userCode}

SELECT lecture_no ,lecture_title ,lecture_file ,frst_wrt_dt ,last_wrt_dt FROM homeschool.tb_lecture
lecture_title LIKE CONCAT('%', #{word}, '%')
AND lecture_no = #{lectureNo} ORDER BY frst_wrt_dt DESC LIMIT #{rowPerPage} OFFSET #{startRow};

mapper.insert:

SELECT user_no
FROM tb_user
WHERE user_code = #{userCode}

INSERT INTO
homeschool.tb_lecture
(
class_no
, lecture_title
, lecture_file
, lecture_status
, frst_wrt_id
, last_wrt_id
)
VALUES
(
#{classNo}
,#{lectureTitle}
,#{lectureFile}
, 'y'
, #{teacherUserNo}
, #{teacherUserNo}
);

mapper.update:
UPDATE
    homeschool.tb_lecture
SET
    lecture_title=#{lectureTitle}
  , lecture_file=#{lectureFile}
  , lecture_status=#{lectureStatus}
  , last_wrt_dt=current_timestamp()
WHERE lecture_no=#{lectureNo};
